import React, {useState} from 'react';

const Forms = () => {
 const [name, setName] = useState('')
    const changeText=(gg)=>{
        setName(gg.target.value)
    }

    return (
        <div className="mt-5 text-center">
        <h1 className="ProColor"> React From Project এর কাজ </h1>
            <p>My From</p>
            <p>{ name }</p>
            <form>
                <input onChange={ changeText } type="text" placeholder="type your name"></input> 
                <input type="submit" value="Submit"></input> 
            </form>
        </div>
    );
};

export default Forms;
