import React from 'react';
import ReactDOM from 'react-dom';


const FindDOMNode = () => {


    function changImg(){
        var component = document.getElementById("imgId");

        ReactDOM.findDOMNode( component ).src="https://www.belightsoft.com/products/imagetricks/img/image-masks.jpg";
    }

    return (
        <div className="mt-5 text-center">
            <h2 className="Color">findDOMNode</h2>
            <p> render() -> ReactDOM.findDOMNode( component ) </p>
            <button onClick={ changImg } className="btn-primary m-2">change image</button>
            <br></br>
            <img id="imgId" src="https://www.remove.bg/assets/inspirations/persons/03-f71e2cac0661eb22d32c073186c50495551166d258983401854f6a1b60e80cb7.jpg"></img>
        </div>
    );
};

export default FindDOMNode;
