import React, { useEffect, useState } from 'react';
import axios from 'axios';


const GetAxios = () => {

    // useState 
    const [ data, setData ] = useState ({
        country: []
    }) 

    // Destructor useState Data
    const { country } = data


    // useEffect Hook GET API Data
    useEffect ( () => {
        const getDidMount = async() => {
            await axios.get(`https://restcountries.eu/rest/v2/all`)
            .then(response => {
                setData({ ...data, country: response.data.slice(0, 3)} )
            })
            .catch(error => {
                console.log(error);
            })
        }
        getDidMount()
    }, [])



    // API Data Declaration 
    const countryList = country.length ? (
        country.map(singleCountry => (
            <div key={singleCountry.id}>
                <h3>{singleCountry.id } Country Name - { singleCountry.name }</h3>
                <p>Capital: {singleCountry.capital}</p>
                <p>Population: {singleCountry.population}</p>
                <p>Time - Zones: {singleCountry.timezones}</p>
                <p>Borders: {singleCountry.borders}</p>
                <img src={singleCountry.flag} alt={singleCountry.title} />
            </div>
        ))
    ) : (
        <div>No Post Found</div>
    )



    return (
        <div className="mt-5 text-center">
            <h1 className="ProColor"> Get Axios এর কাজ </h1>
            <p> Terminal: npm install axios </p>
            {countryList} 
        </div>
    );
};

export default GetAxios;
