import styled from 'styled-components';
import defaultImg from '../images/room-1.jpeg'

const StyledHero = styled.header`
  background: transparent;
  border-radius: 3px;
  border: 2px solid palevioletred;
  color: palevioletred;
  margin: 0 1em;
  padding: 0.25em 1em;
`

export default StyledHero;
