import React from 'react';
import { useState } from 'react';

const UseState = () => {

    const [name, setchangeName] = useState( {
        country: "Pakistan"
    })


    return(
        <div>
            <h1>{name.country}</h1>
            <button onClick={()=>setchangeName({country: "India"})}> click me</button>
        </div>
    );
};

export default UseState
