import React, { useEffect, useState } from 'react';
import axios from 'axios';


const GetAxios = () => {

    const [ data, setData ] = useState ({
        country: []
    }) 

    // Destructor useState Data
    const { country } = data

    useEffect ( () => {
        const getDidMount = async() => {
            const response = await axios.get(`https://restcountries.eu/rest/v2/all`)
            .then(response => {
                setData({ ...data, country: response.data} )
            })
            .catch(error => {
                console.log(error);
            })
        }
        getDidMount()
    }, [])



    const countryList = country.length ? (
        country.map(singleCountry => (
            <div key={singleCountry.id}>
                <h3>{singleCountry.id } - { singleCountry.name }</h3>
                <p>{singleCountry.capital}</p>
            </div>
        ))
    ) : (
        <div>No Post Found</div>
    )



    return (
        <div className="mt-5 text-center">
            <h1 className="ProColor"> Get Axios এর কাজ </h1>
            <h3> {countryList} </h3>
        </div>
    );
};

export default GetAxios;
