import React, { useState } from 'react';


const [name, changeName] = useState( {
        country: "East Pakistan"
    })
const Change = () => {
        changeName( { country: "Bangladesh" } )
        alert("East Pakistan change by Bangladesh")
    }



//functional component er arrow function
const UseState = () => { 







    return(
        <div className="text-center">
            <h1> useState Hook এর কাজ  </h1>
            <h3>{name.country}</h3>
            <button onClick = { Change }> {name.country}</button>
        </div>
    );
};

export default UseState
