import React from 'react';
import { useState } from 'react';

const UseState = () => {

    const [name, changeName] = useState( {
        country: "Pakistan"
    })




    return(
        <div>
            <h1>{name.country}</h1>
            <button onClick = { () => changeName( { country: "India" } ) }> click me</button>
        </div>
    );
};

export default UseState
