import React from 'react';
import { useState } from 'react';

//functional component
const UseState = () => { 

    const [name, changeName] = useState( {
        country: "East Pakistan"
    })


    const Change = () => {
        changeName( { country: "Bangladesh" } )
    }
    const Click = () => {
        alert("East Pakistan change by Bangladesh")
    }




    return(
        <div className="text-center">
            <h1> useState Hook এর কাজ  </h1>
            <h3>{name.country}</h3>
            <button onClick = { Click } onChange = { Change }> {name.country}</button>
        </div>
    );
};

export default UseState
