import React, { useState } from 'react';



const FormValidation = () => {

    const [username, setUsername] = useState()
    const [fatherName, setFatherName] = useState()
    const [motherName, setMotherName] = useState()
    const [age, setAge] = useState()
    const [mobile, setMobile] = useState()
    const [email, setEmail] = useState()


    const OnChangeHandler1=(event)=>{
        setUsername(event.target.value)
    }

    const OnChangeHandler2=(event)=>{
        setFatherName(event.target.value)
    }

    const OnChangeHandler3=(event)=>{
        setMotherName(event.target.value)
    }

    const OnChangeHandler4=(event)=>{
        setAge(event.target.value)
    }

    const OnChangeHandler5=(event)=>{
        setMobile(event.target.value)
    }

    const OnChangeHandler6=(event)=>{
        setEmail(event.target.value)
    }

    return (
        <div className="mt-5 text-center">
        <h1 className="ProColor"> Form Validation From এর কাজ </h1>
            <form>
                <div className="form-group">

                    <p>Your User Name: {username}</p>
                    <p>Your Father Name: {fatherName}</p>
                    <p>Your Mother Name: {motherName}</p>
                    <p>Your Age: {age}</p>
                    <p>Your Mobile Number: {mobile}</p>
                    <p>Your Email Address: {email}</p>

                    <input onChange={ OnChangeHandler1 } name="username" type="text" placeholder="Your Name. .."></input><br/>
                    <input onChange={ OnChangeHandler2 } name="fatherName" type="text" placeholder="Your Father Name..."></input><br/>
                    <input onChange={ OnChangeHandler3 } name="motherName" type="text" placeholder="Your Mother Name..."></input><br/>
                    <input onChange={ OnChangeHandler4 } name="age" type="text" placeholder="Your Age ..."></input><br/>
                    <input onChange={ OnChangeHandler5 } name="mobile" type="text" placeholder="Your Mobile ..."></input><br/>
                    <input onChange={ OnChangeHandler6 } name="email" type="email" placeholder="Your Email ..."></input><br/>

                </div>
            </form>
        </div>
    );
};

export default FormValidation;
