import React, { useEffect, useState } from 'react';
import axios from 'axios';


const PostAxios = () => {

    // useState 
    const [ data, setData ] = useState ({
        postData: " ",
        postResult: " "
    })

    // Destructor useState Data
    const { postData, postResult } = data


    const onChangeHandler = (event) => {
        const myData = event.target.value
        setData(...data, { postData:myData })
    }


    useEffect ( () => {
        const getDidMount = async() => {
            const response =  await axios.post('http://localhost/php7.com/', postData)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            })
        }
        getDidMount()
    },[])

    return (
        <div className="mt-5 text-center">
            <h1 className="ProColor"> Post Axios এর কাজ </h1>

            <input onChange={() => onChangeHandler } type="text" />
            <button> Send </button>
        </div>
    );
};

export default PostAxios;
