import React, { useState } from 'react';



const FormValidation = () => {

    const [username, setUsername] = useState()
    const [fatherName, setFatherName] = useState()
    const [motherName, setMotherName] = useState()
    const [age, setAge] = useState()
    const [mobile, setMobile] = useState()
    const [email, setEmail] = useState()

    const OnChangeHandler=(event)=>{
        setUsername(event.target.value)
        setFatherName(event.target.value)
        setMotherName(event.target.value)
        setAge(event.target.value)
        setMobile(event.target.value)
        setEmail(event.target.value)
    }

    return (
        <div className="mt-5 text-center">
        <h1 className="ProColor"> Form Validation From এর কাজ </h1>
            <form>
                <p>output: {username}</p>
                <input onChange={ OnChangeHandler } name="username" type="text" placeholder="Your Name. .."></input><br/>
                <input onChange={ OnChangeHandler } name="fatherName" type="text" placeholder="Your Father Name..."></input><br/>
                <input onChange={ OnChangeHandler } name="motherName" type="text" placeholder="Your Mother Name..."></input><br/>
                <input onChange={ OnChangeHandler } name="age" type="text" placeholder="Your Age ..."></input><br/>
                <input onChange={ OnChangeHandler } name="mobile" type="text" placeholder="Your Mobile ..."></input><br/>
                <input onChange={ OnChangeHandler } name="email" type="email" placeholder="Your Email ..."></input><br/>
            </form>
        </div>
    );
};

export default FormValidation;
