import React, { Component } from 'react';
import axios from 'axios'

class Api extends Component {

    state = {
        posts: [],
        photos: []
    }

    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then(result => {
                this.setState({
                    posts: result.data.slice(0,10)
                } )
            })
        axios.post('https://jsonplaceholder.typicode.com/photos')
            .then(result => {
                this.setState({
                    ...this.state,
                    photos: result.data.slice(0,20)
                } )
            })
    }
    render() {
        console.log(this.state.photos)
        const postList = this.state.posts.length ? (
            this.state.posts.map(post => (
                <div key={post.id}>
                    <h3>{post.id } - { post.title }</h3>
                    <p>{post.body}</p>
                </div>
            ))
        ) : (
            <div>No Post Found</div>
        )

        const imageList = this.state.photos.map(photo => (
            <div key={photo.id}>
                <img src={photo.thumbnailUrl} alt={photo.title} />
            </div>

        ))





        return ( 

            <div>
                { postList }
                { imageList }
            </div>
        )
    }

}

export default Api
