import React, { useState } from 'react';





const MultiForm = () => {


    const [firstName, setFirstName] = useState()
    const [lastName, setLastName] = useState()
    const [email, setEmail] = useState()
    const [mobile, setMobile] = useState()

    const initialState = {
            firstName: null,
            lastName: null,
            email: null,
            mobile: null
        }

    const [formData, setFormData] = useState(initialState)


    const onChangeHandler=(event)=>{
        // setFormData({...formData, {[e.target.name]: e.target.value }})

        // setFirstName({[inputName]:inputValue})
        // setLastName({[inputName]:inputValue})
        // setEmail({[inputName]:inputValue})
        // setMobile({[inputName]:inputValue})
    }



    return (
        <div className="mt-5 text-center">
        <h1 className="ProColor"> Multiple From এর কাজ </h1>
            <p> First Name : { firstName } </p>
            <p> Last Name : { lastName } </p>
            <p> Email Address : { email } </p>
            <p> Mobile Number : { mobile } </p>
            <form>
                <input onChange={e => setFirstName(e.target.value) } name="firstName" type="text" placeholder="First Name"></input><br></br>
                <input onChange={ e => setLastName(e.target.value) } name="lastName" type="text" placeholder="Last Name"></input><br></br>
                <input onChange={ e => setEmail(e.target.value) } name="email" type="text" placeholder="Email"></input><br></br>
                <input onChange={ e => setMobile(e.target.value) } name="mobile" type="text" placeholder="Mobile"></input><br></br>

                <input onChange={ onChangeHandler } name="firstName" type="text" placeholder="First Name"></input><br></br>
                <input onChange={ onChangeHandler } name="lastName" type="text" placeholder="Last Name"></input><br></br>
                <input onChange={ onChangeHandler } name="email" type="text" placeholder="Email"></input><br></br>
                <input onChange={ onChangeHandler } name="mobile" type="text" placeholder="Mobile"></input><br></br>
                <input type="submit" value="Submit"></input> 
            </form>
        </div>
    );
};

export default MultiForm;
