import React, { useState } from 'react';
import axios from 'axios';


const PostAxios = () => {

    // useState 
    const [ data, setData ] = useState ({
        postData: " ",
        postResult: " "
    })

    // Destructor useState Data
    const { postData, postResult } = data


    const onChangeHandler = (event) => {
        const myData = event.target.value
        setData({...data, postData:myData })
    }

    const onClickHandler = () => {
        const getDidMount = () => {
            axios.post(`http://lancerabir.com/react/`, postData)
            .then(response => {
                setData({...data, postResult: response.data})
                console.log(response);

                alert(postResult)

            })
            .catch(error => {
                console.log(error);
                alert(" post not found ")
            })
        } 
        getDidMount()
    }




    return (
        <div className="mt-5 text-center">
            <h1 className="ProColor"> Post Axios এর কাজ </h1>
                <input onChange={ onChangeHandler } type="text" />
                <button onClick={ onClickHandler }> Send </button>
        </div>
    );
};

export default PostAxios;
