import React, { Component } from 'react'
import './Title.css'

class Title extends Component {


    state = {
        title: 'this is react title1',
        isInput: false
    }

    editHandler() {
        this.setState({
            ...this.state,
            isInput: true
        })
    }

    render() {
        let output = null

        if (this.state.isInput) {
            output = (
                <div>
                    <input className="form-control" type="text" value={this.state.title}/>             
                </div>
            )
        } else {
            output = (
                <div className="d-flex main">
                    <h5 className="display-4"> {this.state.title} </h5>
                    <span onClick={ () => this.editHandler() } className="ml-auto icon">
                        <i className="fas fa-edit"></i>
                    </span>
                </div>
            )
        }
        return (
            <div>
                {output}
            </div>
        )
    }
}
export default Title
