import React from 'react';
import ReactTable from 'react-table';

const Table = () => {


    const tableData = [
        {

        }
    ]
    const columnsHooks = [
        {
            Header: "Name",
            accessor: ""
        },
        {
            Header: "Age",
            accessor: ""
        }
    ]

    return (
        <div className="mt-5 text-center">
            <h1 className="ProColor"> React Table এর কাজ </h1>
            <p> Terminal: npm install react-table </p>

            <ReactTable 
                data={tableData}                             // data বসানোর জন্য
                columns={columnsHooks}                          // কতো গুলা columns হবে ?
                defaultPageSize={2}                 // page size কতো হবে ?
                pageSizeOptions={[2, 4, 6, 8, 10]}  // কতো গুলা row হবে ?
            />



        </div> 
    );
};

export default Table;
