import React, { Component } from 'react'

class Controller extends Component {


    state = {
        start: true,
        pause: false,
        lap: false,
        reset: false
    }

    clickStart(){
        this.setState({
            ...this.state,
            pause: true,
            lap: true,
        })
    }

    render() {
        let output = null

        if (this.state.start && !this.state.reset) {
            output = (
                <div className="py-5">
                    <button 
                    className="btn btn-success btn-lg px-5"
                    onClick = { (event) => this.state.clickStart(event) }
                    >Start</button>
                </div>
            )
        } else {

        }
        return ( 
            <div>
                {output}
            </div>
        )
    }
}
export default Controller
