import React, { useContext, useState } from 'react';
import defaultBcg from '../../images/room-1.jpeg';
import Hero from '../Hero';
import Banner from '../Banner';
import { Link } from 'react-router-dom';
import { RoomContext } from '../../Context';


const SingleRoom = (props) => {

    const [ state, setstate ] = useState({
        slug: props.match.params.slug,
        defaultBcg
    })

    const { getRoom }  = useContext(RoomContext);
    const room = getRoom(state.slug)
    console.log(room);
    if(!room){
        return <div className="error">
            <h3> ! Sorry no such could be found...</h3>
            <Link to="/rooms" className="btn-primary"> back home </Link>
        </div>
    }


    return (
        <section>
            <Hero>
                <Banner title={state.slug} subtitle={`rooms are available here ${room.name} `}>
                    <Link to='/rooms' className="btn-primary"> ours rooms </Link>
                </Banner>
            </Hero>
        </section>
    );
};

export default SingleRoom;
