import React, { createContext, useState, useEffect }  from 'react';
import items from './data';

export const RoomContext = createContext();

// Room Consumer
const RoomConsumer = RoomContext.Consumer;
export { RoomConsumer } ;

const RoomProvider = (props) => {

    const [values, setValues] = useState(
        {
            rooms: [],
            sortedRooms: [],
            featuredRooms: [],
            loading: true, 
            type: "single",
            capacity: 1,
            price: 0,
            minPrice: 0,
            maxPrice: 0,
            minSize: 0,
            maxSize: 0,
            breakfast: false,
            pets: false
        }
    )

    const formatData   = (items) => {
        let tempItems  = items.map( item => {
            let id     = item.sys.id
            let images = item.fields.images.map( image => image.fields.file.url )
            let room   = { ...item.fields, images, id }
            return room
        } )
        return tempItems
    }

    // GetData
    useEffect ( () => {
        let rooms         = formatData(items);
        let featuredRooms = rooms.filter( room => room.featured === true );
        let maxPrice      = Math.max(...rooms.map(item => item.price ) ); 
        let maxSize       = Math.max(...rooms.map(item => item.size ) ); 
        setValues(
            { 
                rooms, 
                featuredRooms, 
                sortedRooms: rooms, 
                loading: false,
                price: maxPrice,
                maxPrice,
                maxSize,
                type: "all",
                capacity: 1,
                minPrice: 0,
                minSize: 0,
                breakfast: false,
                pets: false
            }
            );
    }, [])

    //Change Handler
    const handleChange = (event) => {
        const type     = event.target.type;
        const name     = event.target.name;
        const value    = event.target.value;
        console.log(`this is type:${type}, this is name:${name}, this is value: ${value}`);
    }

    //filter Room
    const filterRooms = () => {
        console.log("hello");
    }


    // getRoom function for pass data in singleRoom.js
    const getRoom     = (slug) => {
        let tempRooms = [...values.rooms]
        const room    = tempRooms.find(room => room.slug === slug)
        return room
    }

    return (
        <RoomContext.Provider value={{ ...values, setValues, getRoom: getRoom, handleChange: handleChange }}> 
            {props.children}
        </RoomContext.Provider>
    );
}


// Higher Order Component
export function withRoomConsumer(Component){
    return function ConsumerWrapper(props){
        return (
            <RoomConsumer>
                { value => <Component {...props} context={value} /> }
            </RoomConsumer> 
        )
    }
}

export default RoomProvider;
