import React, { useState } from 'react';

const FormValidation = () => {

    const [username, setUsername] = useState()

    const OnChangeHandler=(event)=>{
        setUsername(event.target.value)
    }

    return (
        <div className="mt-5 text-center">
        <h1 className="ProColor"> Form Validation From এর কাজ </h1>
            <form>
                <p>output: {username}</p>
                <input onChange={ OnChangeHandler } name="username" type="text" placeholder="Your Name. .."></input><br/>
                <input onChange={ OnChangeHandler } name="username" type="text" placeholder="Your Mother Name..."></input><br/>
                <input onChange={ OnChangeHandler } name="username" type="text" placeholder="Your Age ..."></input><br/>
            </form>
        </div>
    );
};

export default FormValidation;
