import React, { useState } from 'react';





const MultiForm = () => {

    const [firstName, setFirstName] = useState("")
    const [lastName, setLastName] = useState("")
    const [email, setEmail] = useState("")
    const [mobile, setMobile] = useState("")

    const onChangeHandler = () => {
        const inputName = event.target.name
        const inputValue = event.target.value

        this.setState({[inputName]:inputValue})
    }


    return (
        <div className="mt-5 text-center">
        <h1 className="ProColor"> Multiple From এর কাজ </h1>
            <form>
                <input onChange={ onChangeHandler } name="firstName" type="text" placeholder="First Name"></input><br></br>
                <input onChange={ onChangeHandler } name="lastName" type="text" placeholder="Last Name"></input><br></br>
                <input onChange={ onChangeHandler } name="email" type="text" placeholder="Email"></input><br></br>
                <input onChange={ onChangeHandler } name="mobile" type="text" placeholder="Mobile"></input><br></br>
                <input type="submit" value="Submit"></input> 
            </form>
        </div>
    );
};

export default MultiForm;
