export default class RefreshComponent extends Component {



     ClickMe=this.ClickMe.bind(this)

    ClickMe(a){
        this.forceUpdate(a);
    }

    render() {

        return (
            <div>
                <div className="mt-5 text-center">
                <h1 className="ProColor"> Refresh Component এর কাজ </h1>

                    <button onClick={ this.ClickMe.bind(this, forceUpdate() )}> Refresh Component Button </button>
                    <h1>{Math.random()}</h1>
                    <h1>{Math.random()}</h1>
                    <p>{Math.random()}</p>
                </div>
                <p>{Math.random()}</p>
            </div>
        )
    }
}
