import React, { Component } from 'react';
import axios from 'axios'

class Api extends Component {

    state = {
        posts: [],
        photos: [],
        todos: [],
        movies: []
    }

    componentDidMount() {
        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then(result => {
                this.setState({
                    posts: result.data.slice(0,10)
                } )
            })
        axios.get('https://jsonplaceholder.typicode.com/photos')
            .then(result => {
                this.setState({
                    ...this.state,
                    photos: result.data.slice(0,10)
                } )
            })
        axios.get('https://jsonplaceholder.typicode.com/todos')
            .then(result => {
                this.setState({
                    ...this.state,
                    todos: result.data.slice(0,10)
                } )
            })
        axios.get('https://api.themoviedb.org/3/movie/157336?api_key=e488192aa452504bf20c057f9cfbcb5c')
            .then(dd => {
                this.setState({
                    ...this.state,
                    movies: dd.data
                } )
                console.log(dd)
            })

    }
    render() {
        console.log(this.state.photos)
        const postList = this.state.posts.length ? (
            this.state.posts.map(post => (
                <div key={post.id}>
                    <h3>{post.id } - { post.title }</h3>
                    <p>{post.body}</p>
                </div>
            ))
        ) : (
            <div>No Post Found</div>
        )

        const imageList = this.state.photos.length ? (
            this.state.photos.map(photo => (
            <div key={photo.id}>
                <img src={photo.thumbnailUrl} alt={photo.title} />
            </div>

        )) 
            ) : (
            <div>No Post Found</div>
        )

        const todosList = this.state.todos.length ? (
            this.state.todos.map(todo => (
            <div key={todo.id}>
                <h2> {todo.title} </h2>
            </div>

        )) 
            ) : (
            <div>No Post Found</div>
        )

        const { title, status, tagline, overview } = this.state.movies

        const movieItem = this.state.movies  (
            <div>
                <h2> {title} - {status} - {tagline} - {overview} </h2>
            </div>

        ) 




        return ( 

            <div>
                { postList }
                { imageList }
                { todosList }
                { movieItem }

                {this.state.movies.title}
            </div>
        )
    }

}

export default Api
