import React from 'react';
import { useState } from 'react';

const UseState = () => {

    const [name, changeName] = useState( {
        country: "Pakistan"
    })


    const Change = () => {
        changeName( { country: "Holand" } )
    }




    return(
        <div>
            <h1>{name.country}</h1>
            <button onClick = { Change } > {name.country}</button>
        </div>
    );
};

export default UseState
